function dConnectAccount(){
	$("#CreateAccount").hide();
	$("#ConnectAccount").show();
}

function dCreateAccount(){
	$("#ConnectAccount").hide();
	$("#CreateAccount").show();
}

dConnectAccount();

function recherche(txt)
{
	var tab=document.querySelectorAll("li.links-list");

	if (txt.length>2)
	{
	
	for (i=0;i<tab.length;i++)
	{
		if (((tab[i].innerText).toLowerCase()).indexOf(txt.toLowerCase())==-1)
		{
			tab[i].style.display='none';
		}
		else
		{
			tab[i].style.display='';
		}
		
		
	}
	console.log(tab);
	}
	else
	{
		
	for (i=0;i<tab.length;i++)
	{

			tab[i].style.display='';
	}
}
}
